import React from "react";

const About = () => {
  return (
    <section id="about" className="py-16 bg-gray-800">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center text-blue-500 my-8">About Me</h2>
        <p className="text-gray-300 max-w-auto">
        I am a passionate Full Stack Developer with expertise in the MERN stack. I love building scalable and efficient web applications that solve real-world problems. With a strong foundation in JavaScript, React, Node.js, and MongoDB, I strive to create seamless user experiences. I am proficient in designing responsive and intuitive user interfaces, developing robust backend systems, and integrating RESTful APIs. My goal is to deliver high-quality, maintainable, and performant solutions that meet both user needs and business objectives. I am constantly learning and adapting to new technologies to stay ahead in the ever-evolving world of web development. Whether it's crafting pixel-perfect designs or optimizing database queries, I am committed to delivering excellence in every aspect of my work.
        </p>
      </div>
    </section>
  );
};

export default About;
